import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';

const SUPABASE_URL = 'https://vhztoaoderjbeientrtn.supabase.co';
const SUPABASE_ANON_KEY = 'sb_publishable_1a9vPsiBaUTqk5diLkpLQA_AWVa3J3n'; 

const supabase = createClient(SUPABASE_URL, SUPABASE_ANON_KEY);

function Home({ user, onLogout }) {
  const [data, setData] = useState([]);      // will hold messages or quotes depending on button clicked
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showTable, setShowTable] = useState(false);
  const [tableType, setTableType] = useState(''); // 'messages' or 'quotes'

  // Fetch messages on Messages button click
  const handleMessages = async () => {
    setLoading(true);
    setError(null);
    setShowTable(false);
    setTableType('');
    try {
      const { data, error } = await supabase
        .from('messages')
        .select('*');

      if (error) throw error;

      setData(data);
      setShowTable(true);
      setTableType('messages');
    } catch (err) {
      setError('Failed to fetch messages: ' + err.message);
    }
    setLoading(false);
  };

  // Fetch quotes on Requests button click
  const handleRequests = async () => {
    setLoading(true);
    setError(null);
    setShowTable(false);
    setTableType('');
    try {
      const { data, error } = await supabase
        .from('quotes')
        .select('*');

      if (error) throw error;

      setData(data);
      setShowTable(true);
      setTableType('quotes');
    } catch (err) {
      setError('Failed to fetch quotes: ' + err.message);
    }
    setLoading(false);
  };

  return (
    <div className="container home-container">
      <h1>Welcome, {user.username} (Admin)</h1>
      <div className="button-group">
        <button onClick={handleMessages}>Messages</button>
        <button onClick={handleRequests}>Requests</button>
        <button onClick={onLogout} className="logout-btn">
          Logout
        </button>
      </div>

      {loading && <p>Loading {tableType}...</p>}
      {error && <p className="error">{error}</p>}

      {showTable && data.length > 0 && (
        <div className="table-wrapper">
          <table>
            <thead>
              <tr>
                {Object.keys(data[0]).map((col) => (
                  <th key={col}>{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.map((item, idx) => (
                <tr key={idx}>
                  {Object.values(item).map((val, i) => (
                    <td key={i}>{val?.toString()}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {showTable && data.length === 0 && <p>No {tableType} found.</p>}
    </div>
  );
}

export default Home;
