import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { createClient } from '@supabase/supabase-js';
import './Login.css';

// Replace with your actual Supabase project info
const SUPABASE_URL = 'https://vhztoaoderjbeientrtn.supabase.co';
const SUPABASE_ANON_KEY = 'sb_publishable_1a9vPsiBaUTqk5diLkpLQA_AWVa3J3n';

const supabase = createClient(SUPABASE_URL, SUPABASE_ANON_KEY);

function Login({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Fetch user with matching username
      const { data: user, error: fetchError } = await supabase
        .from('users')
        .select('*')
        .eq('username', username)
        .single();

      if (fetchError) {
        setError('Login failed: ' + fetchError.message);
        return;
      }

      if (!user || user.password !== password) {
        setError('Invalid username or password');
        return;
      }

      if (user.role !== 'admin') {
        setError('Access denied: only Admins can login here');
        return;
      }

      // Login success
      onLogin(user);
    } catch (err) {
      setError('Unexpected error: ' + err.message);
    }
  };

  return (
    <div className="container login-container">
      <form onSubmit={handleSubmit} className="form-box">
        <h2>Admin Login</h2>
        {error && <p className="error">{error}</p>}
        <label>Username</label>
        <input
          type="text"
          value={username}
          onChange={e => setUsername(e.target.value)}
          required
        />
        <label>Password</label>
        <input
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        <p>
          Don't have an account?{' '}
          <Link to="/register" className="link">
            Register here
          </Link>
        </p>
      </form>
    </div>
  );
}

export default Login;
