import React, { useState } from "react";
import { createClient } from "@supabase/supabase-js";
import { User } from "./types";

type LoginProps = {
  onLogin: (user: User) => void;
};

const SUPABASE_URL = "https://vhztoaoderjbeientrtn.supabase.co";
const SUPABASE_ANON_KEY = "sb_publishable_1a9vPsiBaUTqk5diLkpLQA_AWVa3J3n";
const supabase = createClient(SUPABASE_URL, SUPABASE_ANON_KEY);

function Login({ onLogin }: LoginProps) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const { data: user, error: fetchError } = await supabase
        .from("users")
        .select("*")
        .eq("username", username)
        .single();

      if (fetchError) {
        setError("Login failed: " + fetchError.message);
        setLoading(false);
        return;
      }

      if (!user || user.password !==
password) {
setError("Invalid username or password");
setLoading(false);
return;
}
  if (user.role !== "admin") {
    setError("Access denied: only Admins can login here");
    setLoading(false);
    return;
  }

  onLogin(user as User);
} catch (err: any) {
  setError("Unexpected error: " + err.message);
} finally {
  setLoading(false);
}
};

return (
<div className="login-container">
<div className="login-card">
<h2>Welcome Back</h2>
{error && <div className="login-error">{error}</div>}
<form onSubmit={handleSubmit}>
<input
type="text"
value={username}
onChange={(e) => setUsername(e.target.value)}
placeholder="Username"
required
/>
<input
type="password"
value={password}
onChange={(e) => setPassword(e.target.value)}
placeholder="Password"
required
/>
<button type="submit" disabled={loading}>
{loading ? "Logging in..." : "Login"}
</button>
</form>
</div>

