import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';

const SUPABASE_URL = 'https://vhztoaoderjbeientrtn.supabase.co';
const SUPABASE_ANON_KEY = 'sb_publishable_1a9vPsiBaUTqk5diLkpLQA_AWVa3J3n'; 

const supabase = createClient(SUPABASE_URL, SUPABASE_ANON_KEY);

function Home({ user, onLogout }) {
  const [quotes, setQuotes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showTable, setShowTable] = useState(false);

  const handleQuotes = () => {
    alert('Quotes button clicked!');
  };

  const handleRequests = async () => {
    setLoading(true);
    setError(null);
    setShowTable(false);
    try {
      let { data, error } = await supabase
        .from('quotes')
        .select('*');

      if (error) throw error;

      setQuotes(data);
      setShowTable(true);
    } catch (err) {
      setError('Failed to fetch quotes: ' + err.message);
    }
    setLoading(false);
  };

  return (
    <div className="container home-container">
      <h1>Welcome, {user.username} (Admin)</h1>
      <div className="button-group">
        <button onClick={handleQuotes}>Quotes</button>
        <button onClick={handleRequests}>Requests</button>
        <button onClick={onLogout} className="logout-btn">
          Logout
        </button>
      </div>

      {loading && <p>Loading requests...</p>}
      {error && <p className="error">{error}</p>}

      {showTable && quotes.length > 0 && (
        <div className="table-wrapper">
          <table>
            <thead>
              <tr>
                {/* Adjust columns below to your table structure */}
                {Object.keys(quotes[0]).map((col) => (
                  <th key={col}>{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {quotes.map((quote, idx) => (
                <tr key={idx}>
                  {Object.values(quote).map((val, i) => (
                    <td key={i}>{val?.toString()}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {showTable && quotes.length === 0 && <p>No requests found.</p>}
    </div>
  );
}

export default Home;
