import React, { useState } from "react";
import { createClient } from "@supabase/supabase-js";
import { useNavigate } from "react-router-dom";
import { User, DataRow } from "./types"; // import the types

const SUPABASE_URL = "https://vhztoaoderjbeientrtn.supabase.co";
const SUPABASE_ANON_KEY = "sb_publishable_1a9vPsiBaUTqk5diLkpLQA_AWVa3J3n";

const supabase = createClient(SUPABASE_URL, SUPABASE_ANON_KEY);

interface HomeProps {
  user: User;
  onLogout: () => void;
}

const Home: React.FC<HomeProps> = ({ user, onLogout }) => {
  const navigate = useNavigate();
  const [data, setData] = useState<DataRow[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [tableType, setTableType] = useState<string>("");
  const [searchTerm, setSearchTerm] = useState("");
  const [editingId, setEditingId] = useState<number | null>(null);
  const [editRow, setEditRow] = useState<DataRow>({ id: 0 });

  const fetchData = async (type: string) => {
    setLoading(true);
    setError(null);
    setTableType(type);
    try {
      const { data: fetchedData, error } = await supabase
        .from(type)
        .select("*")
        .order("id", { ascending: true });
      if (error) throw error;
      setData(fetchedData as DataRow[]);
    } catch (err: unknown) {
      setError(`Failed to fetch ${type}: ${(err as Error).message}`);
    }
    setLoading(false);
  };

  const handleDelete = async (id: number) => {
    if (!window.confirm("Are you sure you want to delete this record?")) return;
    try {
      const { error } = await supabase.from(tableType).delete().eq("id", id);
      if (error) throw error;
      setData(data.filter((item) => item.id !== id));
    } catch (err: unknown) {
      alert("Delete failed: " + (err as Error).message);
    }
  };

  const handleEdit = (item: DataRow) => {
    setEditingId(item.id);
    setEditRow(item);
  };

  const handleSave = async () => {
    if (editingId === null) return;
    try {
      const { error } = await supabase
        .from(tableType)
        .update(editRow)
        .eq("id", editingId);
      if (error) throw error;
      setData(data.map((row) => (row.id === editingId ? editRow : row)));
      setEditingId(null);
    } catch (err: unknown) {
      alert("Update failed: " + (err as Error).message);
    }
  };

  const handleCancel = () => {
    setEditingId(null);
  };

  const handleCreateAccount = () => {
    navigate("/register");
  };

  const filteredData = data.filter((item) =>
    Object.values(item)
      .join(" ")
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  return (
    <div className="admin-container">
      <div className="main-header">SparkleSmart Technologies</div>

      <header className="admin-header">
        <h1>Admin Dashboard</h1>
        <p>Welcome, {user.username}</p>
        <div>
          <button onClick={() => fetchData("messages")}>Messages</button>
          <button onClick={() => fetchData("quotes")}>Requests</button>
          <button onClick={handleCreateAccount} className="create-account-btn">
            Create Account
          </button>
          <button onClick={onLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </header>

      {loading && <p>Loading {tableType}...</p>}
      {error && <p className="error">{error}</p>}

      {tableType && !loading && (
        <>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search records..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          {filteredData.length > 0 ? (
            <table className="admin-table">
              <thead>
                <tr>
                  {Object.keys(filteredData[0]).map((col) => (
                    <th key={col}>{col}</th>
                  ))}
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredData.map((item) => (
                  <tr key={item.id}>
                    {Object.keys(item).map((col) => (
                      <td key={col}>
                        {editingId === item.id ? (
                          <input
                            value={editRow[col] ?? ""}
                            onChange={(e) =>
                              setEditRow({ ...editRow, [col]: e.target.value })
                            }
                          />
                        ) : (
                          item[col]?.toString()
                        )}
                      </td>
                    ))}
                    <td>
                      {editingId === item.id ? (
                        <>
                          <button onClick={handleSave}>Save</button>
                          <button onClick={handleCancel}>Cancel</button>
                        </>
                      ) : (
                        <>
                          <button onClick={() => handleEdit(item)}>Edit</button>
                          <button
                            className="delete-btn"
                            onClick={() => handleDelete(item.id)}
                          >
                            Delete
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No {tableType} found.</p>
          )}
        </>
      )}

      <footer className="admin-footer">
        &copy; {new Date().getFullYear()} SparkleSmart Technologies. All rights reserved.
      </footer>
    </div>
  );
};

export default Home;
