import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Home({ user, onLogout }) {
  const navigate = useNavigate();

  // ... your existing states & functions here, or keep minimal for testing

  const handleCreateAccount = () => {
    navigate("/register"); // navigate programmatically to register page
  };

  return (
    <div className="admin-container">
      <div className="main-header">SparkleSmart Technologies</div>

      <header className="admin-header">
        <h1>Admin Dashboard</h1>
        <p>Welcome, {user.username}</p>
        <div>
          <button onClick={() => alert("Fetch Messages")}>Messages</button>
          <button onClick={() => alert("Fetch Requests")}>Requests</button>
          <button onClick={handleCreateAccount} className="create-account-btn">
            Create Account
          </button>
          <button onClick={onLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </header>

      <footer className="admin-footer">
        &copy; {new Date().getFullYear()} SparkleSmart Technologies. All rights reserved.
      </footer>

      <style jsx>{`
        .main-header {
          font-size: 28px;
          font-weight: bold;
          color: white;
          padding: 15px 20px;
          text-align: center;
          background-color: #00b3a4;
          margin-bottom: 20px;
          user-select: none;
          border-radius: 6px;
        }
        .admin-container {
          padding: 20px;
          font-family: Arial, sans-serif;
          min-height: 100vh;
          display: flex;
          flex-direction: column;
        }
        .admin-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 15px;
          flex-wrap: wrap;
        }
        button {
          margin: 5px;
          padding: 8px 12px;
          border: none;
          background: linear-gradient(90deg, #00b3a4, #d32f2f);
          color: white;
          cursor: pointer;
          border-radius: 5px;
          transition: background 0.3s ease;
        }
        button:hover {
          background: linear-gradient(90deg, #009e92, #b62828);
        }
        .create-account-btn {
          background-color: #00b3a4;
        }
        .create-account-btn:hover {
          background-color: #009e92;
        }
        .logout-btn {
          background: linear-gradient(90deg, #d32f2f, #00b3a4);
        }
        .logout-btn:hover {
          background: linear-gradient(90deg, #b62828, #009e92);
        }
        .admin-footer {
          margin-top: auto;
          padding: 15px 20px;
          background-color: #00b3a4;
          color: white;
          text-align: center;
          border-radius: 0 0 8px 8px;
          user-select: none;
        }
      `}</style>
    </div>
  );
}

export default Home;
